import {
  Container,
  Box,
  Flex,
  useTheme,
  Avatar,
  Heading,
  Text,
  Stack,
  StackDivider,
  Button,
  useDisclosure
} from "@chakra-ui/react";

import { Subscription } from "./Subscription";
import netflixImage from "../images/netflix.png";
import spotify from "../images/spotify.png";
import disney from "../images/disney.png";
import NewGroup from './NewGroup'



export default function GroupList() {
  const theme = useTheme();
  const { isOpen, onOpen, onClose } = useDisclosure()

  const subscriptionCost = "$8.10";
  const savings = "$14.90";
  const profilePicture = "https://bit.ly/sage-adebayo";
  const subscriptions = [
    {
      name: "Netflix",
      image: netflixImage,
      cost: "$3.75",
      members: "3/5",
    },
    {
      name: "Spotify",
      image: spotify,
      cost: "$4.33",
      members: "2/6",
    },
  ];

  const friendSubscriptions = [
    {
      name: "Disney",
      image: disney,
      cost: "3.75",
      members: "3/6",
    },
  ];

  return (
    <Container >
      <Box
        className="Header"
        w="100%"
        p={4}
      >
        <Heading as="h1" size="md" textAlign="left">
          Groups
        </Heading>
      </Box>
      <Flex className="Profile" margin="10px">
        <Avatar src={profilePicture} marginRight="10px" />
        <Box>
          <Text fontWeight="bold"> Total Subscriptions</Text>
          <Text>
            You owe{" "}
            <Text as="span" fontWeight="bold">
              {subscriptionCost}
            </Text>
            /month
          </Text>
          <Text fontWeight="bold" color="green">
            {savings}
            <Text as="span" fontSize="xs" color="black" fontWeight="thin">
              {" "}
              Saved
            </Text>
          </Text>
        </Box>
      </Flex>
      <Stack
        className="MyGroup"
        direction={"column"}
        bg="white"
        divider={<StackDivider borderColor="gray.200" />}
        spacing={4}
        align="stretch"
        borderRadius={'md'}
        padding="2"
      >
      <Flex align="center" justify="space-between">
        <Box>
          <Text fontWeight="bold">My Groups</Text>
        </Box>
        <Box>
          <Button colorScheme="blue" onClick={onOpen}>New Group</Button>
        </Box>
      </Flex>

        {subscriptions.map((subscription) => (
          <Subscription
            image={subscription.image}
            cost={subscription.cost}
            name={subscription.name}
            members={subscription.members}
          />
        ))}
        <Text fontWeight="bold">Friend Groups</Text>
        {friendSubscriptions.map((subscription) => (
          <Subscription
            image={subscription.image}
            cost={subscription.cost}
            name={subscription.name}
            members={subscription.members}
          />
        ))}
      </Stack>

      {isOpen && <NewGroup onClose={onClose}/>}

    </Container>
  );
}
